package me.mosuji.nammsamm.springbootdeveloper;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;@SpringBootApplicationpublic class SpringBootDeveloperApplication {    public static void main(String[] args) {        /*        SpringApplication.run() : 애플리케이션을 실행.        첫 인수 : 메인 클래스로 사용할 클래스.        두 번째 인수 : 커맨드 라인의 인수들을 전달.         */        SpringApplication.run(SpringBootDeveloperApplication.class,args);    }}